import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-moment'; // Import moment adapter for Chart.js
import { Chart, TimeScale } from 'chart.js';
import 'chart.js/auto';

Chart.register(TimeScale); // Register the time scale

function SensorChart({ data, onPointClick }) {
    const chartData = {
        labels: data.map(d => new Date(d.timestamp)),
        datasets: [
            {
                label: 'Temperature (째C)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.temperature })),
                fill: false,
                borderColor: 'red',
                yAxisID: 'y_temp', // Explicitly use the left y-axis for temperature
            },
            {
                label: 'Humidity (%)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.humidity })),
                fill: false,
                borderColor: 'blue',
                yAxisID: 'y_humidity', // Explicitly use the right y-axis for humidity
            },
        ],
    };

    const options = {
        scales: {
            x: {
                type: 'time',
                time: {
                    unit: 'day',
                    displayFormats: {
                        day: 'MM/DD/YYYY', // Display the full date
                    },
                },
                ticks: {
                    autoSkip: false, // Ensure dates are displayed even for a single point
                },
            },
            y_temp: { // Define the left y-axis for temperature
                type: 'linear',
                position: 'left',
                min: -50, // Fixed minimum value for temperature
                max: 50,  // Fixed maximum value for temperature
                ticks: {
                    stepSize: 10, // Interval for tick marks
                },
                title: {
                    display: true,
                    text: 'Temperature (째C)',
                },
                grid: {
                    drawOnChartArea: true, // Show grid lines for this axis
                },
            },
            y_humidity: { // Define the right y-axis for humidity
                type: 'linear',
                position: 'right',
                min: 0,  // Fixed minimum value for humidity
                max: 100, // Fixed maximum value for humidity
                ticks: {
                    stepSize: 10, // Interval for tick marks
                },
                grid: {
                    drawOnChartArea: false, // Avoid grid lines on the chart area for this axis
                },
                title: {
                    display: true,
                    text: 'Humidity (%)',
                },
            },
        },
        responsive: true,
        maintainAspectRatio: false,
        onClick: (event, elements) => {
            if (elements.length > 0) {
                const index = elements[0].index;
                onPointClick(index);
            }
        },
    };

    return (
        <div style={{ height: '400px' }}>
            <Line data={chartData} options={options} />
        </div>
    );
}

export default SensorChart;
