import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import SensorChart from './components/SensorChart';
import DataInputForm from './components/DataInputForm';
import DataTable from './components/DataTable';

const socket = io(process.env.REACT_APP_BACKEND_URL);

function App() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data`)
            .then(response => response.json())
            .then(data => setData(data))
            .catch(err => setError('Failed to fetch initial data'));

        socket.on('update', newData => {
            setData(prevData => [...prevData, newData]);
        });

        socket.on('error', errorMsg => {
            setError(errorMsg.error);
        });

        return () => {
            socket.disconnect();
        };
    }, []);

    const handleEdit = (index, updatedData) => {
        const newData = [...data];
        newData[index] = { ...newData[index], ...updatedData };
        setData(newData);
        // Optional: Emit update to backend if needed
    };

    const handleDelete = (index) => {
        const newData = data.filter((_, i) => i !== index);
        setData(newData);
        // Optional: Emit delete to backend if needed
    };

    return (
        <div className="App">
            <h1>Real-Time Environmental Data Monitoring</h1>
            {error && data.length === 0 && <p className="error">{error}</p>}
            <SensorChart data={data} />
            <DataInputForm />
            <DataTable data={data} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
    const [selectedIndex, setSelectedIndex] = useState(null);

    const handlePointClick = (index) => {
        setSelectedIndex(index);
    };

    return (
        <div className="App">
            {/*...(existing code)*/}
            <SensorChart data={data} onPointClick={handlePointClick} />
            <DataInputForm />
            <DataTable data={data} onEdit={handleEdit} onDelete={handleDelete} selectedIndex={selectedIndex} />
        </div>
    );
}

export default App;
