import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io(process.env.REACT_APP_BACKEND_URL);

function DataInputForm() {
    const [temperature, setTemperature] = useState('');
    const [humidity, setHumidity] = useState('');
    const [timestamp, setTimestamp] = useState('');
    const [message, setMessage] = useState('');
    const [existingTimestamps, setExistingTimestamps] = useState([]);

    // Fetch existing data to store timestamps
    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data`)
            .then(response => response.json())
            .then(data => {
                const timestamps = data.map(entry => new Date(entry.timestamp).toISOString());
                setExistingTimestamps(timestamps);
            })
            .catch(error => {
                console.error('Failed to fetch initial data', error);
            });
    }, []);

    // Set the default timestamp to current time when the component loads
    useEffect(() => {
        const currentTime = new Date().toISOString().slice(0, 16);
        setTimestamp(currentTime);
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!temperature || !humidity) {
            setMessage('Please enter both temperature and humidity.');
            return;
        }

        // Validation: Check if temperature and humidity are within the allowed range
        if (temperature < -50 || temperature > 50) {
            alert('Temperature out of range! Please input a value between -50 and 50.');
            return;
        }

        if (humidity < 0 || humidity > 100) {
            alert('Humidity out of range! Please input a value between 0 and 100.');
            return;
        }

        // Check for duplicate timestamp
        const formattedTimestamp = timestamp || new Date().toISOString();
        if (existingTimestamps.includes(new Date(formattedTimestamp).toISOString())) {
            alert('This timestamp already exists. Please select a different timestamp.');
            return;
        }

        // Create a data object
        const newData = {
            temperature: parseFloat(temperature),
            humidity: parseFloat(humidity),
            timestamp: formattedTimestamp,
        };

        // Emit the new data through the socket
        socket.emit('new_data', newData);

        // Update existing timestamps
        setExistingTimestamps(prevTimestamps => [...prevTimestamps, formattedTimestamp]);

        // Clear form inputs
        setTemperature('');
        setHumidity('');
        setTimestamp(new Date().toISOString().slice(0, 16)); // Reset to current time
        setMessage('Data submitted successfully!');
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Temperature:</label>
                    <input
                        type="number"
                        value={temperature}
                        onChange={(e) => setTemperature(e.target.value)}
                        className="input-box"
                        step="0.1"
                    />
                </div>
                <div>
                    <label>Humidity:</label>
                    <input
                        type="number"
                        value={humidity}
                        onChange={(e) => setHumidity(e.target.value)}
                        className="input-box"
                        step="0.1"
                    />
                </div>
                <div>
                    <label>Timestamp (optional):</label>
                    <input
                        type="datetime-local"
                        value={timestamp}
                        onChange={(e) => setTimestamp(e.target.value)}
                        className="input-box"
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default DataInputForm;
