import React from 'react';
import { Line } from 'react-chartjs-2';

function SensorChart({ data }) {
    const chartData = {
        labels: data.map(d => new Date(d.timestamp).toLocaleTimeString()),
        datasets: [
            {
                label: 'Temperature (°C)',
                data: data.map(d => d.temperature),
                fill: false,
                borderColor: 'red',
            },
            {
                label: 'Humidity (%)',
                data: data.map(d => d.humidity),
                fill: false,
                borderColor: 'blue',
            },
        ],
    };

    const options = {
        scales: {
            y: {
                min: -100,  // Set minimum y-axis range
                max: 100,   // Set maximum y-axis range
            },
        },
        responsive: true,
        maintainAspectRatio: false, // This allows resizing the chart to fit the container
    };

    return (
        <div style={{ height: '400px' }}> {/* Adjust height as needed */}
            <Line data={chartData} options={options} />
        </div>
    );
}

export default SensorChart;
