import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-moment'; // Import moment adapter for Chart.js
import { Chart, TimeScale } from 'chart.js';
import 'chart.js/auto';

Chart.register(TimeScale); // Register the time scale

function SensorChart({ data, onPointClick }) {
    const chartData = {
        labels: data.map(d => new Date(d.timestamp)),
        datasets: [
            {
                label: 'Temperature (째C)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.temperature })),
                fill: false,
                borderColor: 'red',
                yAxisID: 'y-temp', // Use this ID to link to the temperature y-axis
            },
            {
                label: 'Humidity (%)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.humidity })),
                fill: false,
                borderColor: 'blue',
                yAxisID: 'y-humidity', // Use this ID to link to the humidity y-axis
            },
        ],
    };

    const options = {
        scales: {
            x: {
                type: 'time',
                time: {
                    unit: 'day',
                    tooltipFormat: 'DD/MM/YY HH:mm',
                },
            },
            y: {
                id: 'y-temp',
                type: 'linear',
                position: 'left',
                min: -50, // Explicitly set the minimum value
                max: 50,  // Explicitly set the maximum value
                ticks: {
                    stepSize: 10, // Set the step size
                },
                title: {
                    display: true,
                    text: 'Temperature (째C)',
                },
                beginAtZero: false, // Ensure the axis does not begin at zero unless specified
            },
            y1: {
                id: 'y-humidity',
                type: 'linear',
                position: 'right',
                min: 0,  // Explicitly set the minimum value
                max: 100, // Explicitly set the maximum value
                ticks: {
                    stepSize: 10, // Set the step size
                },
                grid: {
                    drawOnChartArea: false, // Avoid grid lines overlapping
                },
                title: {
                    display: true,
                    text: 'Humidity (%)',
                },
                beginAtZero: true, // Ensures the axis starts at zero
            },
        },
        responsive: true,
        maintainAspectRatio: false,
        onClick: (event, elements) => {
            if (elements.length > 0) {
                const index = elements[0].index;
                onPointClick(index);
            }
        },
    };

    return (
        <div style={{ height: '400px' }}>
            <Line data={chartData} options={options} />
        </div>
    );
}

export default SensorChart;
