// frontend/src/App.js
import React, { useEffect, useState } from 'react';
import SensorChart from './components/SensorChart';
import DataInputForm from './components/DataInputForm';
import DataTable from './components/DataTable';
import './App.css';
import socket from './socket';

function App() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [selectedIndex, setSelectedIndex] = useState(null);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                data.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
                setData(data);
            })
            .catch(err => {
                console.error('Failed to fetch initial data:', err);
                setError('Failed to fetch initial data');
            });

        socket.on('new_data', newData => {
            setData(prevData => {
                const updatedData = [...prevData, newData];
                updatedData.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
                return updatedData;
            });
        });

        socket.on('error', errorMsg => {
            setError(errorMsg.error);
        });

        return () => {
            socket.disconnect();
        };
    }, []);

    const handleDelete = (index) => {
        const itemToDelete = data[index];
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data/${itemToDelete._id}`, {
            method: 'DELETE',
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to delete data');
            }
            setData(prevData => prevData.filter((_, i) => i !== index));
        })
        .catch(err => {
            console.error('Failed to delete data:', err);
            setError('Failed to delete data');
        });
    };

    const handleEdit = (index, updatedData) => {
        const newData = [...data];
        newData[index] = { ...newData[index], ...updatedData };
        newData.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
        setData(newData);
        setSelectedIndex(null);
    };

    const handlePointClick = (index) => {
        setSelectedIndex(selectedIndex === index ? null : index);
    };

    const handleRowClick = (index) => {
        if (selectedIndex === index) {
            setSelectedIndex(null);
        } else {
            setSelectedIndex(index);
        }
    };

    return (
        <div className="App">
            <h1>Environmental Data Monitoring</h1>
            {error && data.length === 0 && <p className="error">{error}</p>}
            <SensorChart data={data} onPointClick={handlePointClick} />
            <DataInputForm />
            <DataTable
                data={data}
                onEdit={handleEdit}
                onDelete={handleDelete}
                selectedIndex={selectedIndex}
                onRowClick={handleRowClick}
            />
        </div>
    );
}

export default App;
