import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-moment'; // Import moment adapter for Chart.js
import { Chart, TimeScale } from 'chart.js';
import 'chart.js/auto';

Chart.register(TimeScale); // Register the time scale

function SensorChart({ data, onPointClick }) {
    const chartData = {
        labels: data.map(d => new Date(d.timestamp)),
        datasets: [
            {
                label: 'Temperature (째C)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.temperature })),
                fill: false,
                borderColor: 'red',
                yAxisID: 'y-humidity', // Use the same yAxisID to combine into one axis
            },
            {
                label: 'Humidity (%)',
                data: data.map(d => ({ x: new Date(d.timestamp), y: d.humidity })),
                fill: false,
                borderColor: 'blue',
                yAxisID: 'y-humidity', // Use the same yAxisID to combine into one axis
            },
        ],
    };

    const options = {
        scales: {
            x: {
                type: 'time',
                time: {
                    unit: 'day',
                    tooltipFormat: 'DD/MM/YY HH:mm',
                },
            },
            y: {
                display: false, // Hide the left y-axis
            },
            y1: {
                id: 'y-humidity',
                type: 'linear',
                position: 'right',
                min: -50,  // Set the minimum to cover both temperature and humidity range
                max: 100,  // Set the maximum to cover both temperature and humidity range
                ticks: {
                    stepSize: 10, // Set the step size
                },
                grid: {
                    drawOnChartArea: true, // Draw the grid lines
                },
                title: {
                    display: true,
                    text: 'Temperature (째C) / Humidity (%)',
                },
            },
        },
        responsive: true,
        maintainAspectRatio: false,
        onClick: (event, elements) => {
            if (elements.length > 0) {
                const index = elements[0].index;
                onPointClick(index);
            }
        },
    };

    return (
        <div style={{ height: '400px' }}>
            <Line data={chartData} options={options} />
        </div>
    );
}

export default SensorChart;
