// frontend/src/App.js
import React, { useEffect, useState } from 'react';
import SensorChart from './components/SensorChart';
import DataInputForm from './components/DataInputForm';
import DataTable from './components/DataTable';
import './App.css'; // Ensure the CSS file is imported
import socket from './socket'; // Use the imported socket instance

function App() {
    // Declare hooks at the top level
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [selectedIndex, setSelectedIndex] = useState(null); // Selected index state for highlighting rows

    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                // Sort the data based on timestamp (earliest to latest)
                data.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
                setData(data);
            })
            .catch(err => {
                console.error('Failed to fetch initial data:', err);
                setError('Failed to fetch initial data');
            });

        // Listen for new data events from the socket
        socket.on('new_data', newData => {
            setData(prevData => {
                const updatedData = [...prevData, newData];
                // Sort the data based on timestamp (earliest to latest)
                updatedData.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
                return updatedData;
            });
        });

        socket.on('error', errorMsg => {
            setError(errorMsg.error);
        });

        // Clean up socket connection on component unmount
        return () => {
            socket.disconnect();
        };
    }, []);

    // Function to handle edits
    const handleEdit = (index, updatedData) => {
        const newData = [...data];
        newData[index] = { ...newData[index], ...updatedData };
        // Sort the data based on timestamp (earliest to latest)
        newData.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
        setData(newData);
        setSelectedIndex(null); // Exit edit mode
    };

    // Function to handle deletions
    const handleDelete = (index) => {
        const newData = data.filter((_, i) => i !== index);
        setData(newData);
        setSelectedIndex(null); // Clear the selection after deletion
    };

    // Function to handle point click on the chart
    const handlePointClick = (index) => {
        setSelectedIndex(selectedIndex === index ? null : index); // Deselect if the same point is clicked
    };

    // Function to handle row click in the table
    const handleRowClick = (index) => {
        if (selectedIndex === index) {
            setSelectedIndex(null); // Deselect if the same row is clicked
        } else {
            setSelectedIndex(index);
        }
    };

    return (
        <div className="App">
            <h1>Environmental Data Monitoring</h1>
            {error && data.length === 0 && <p className="error">{error}</p>}
            <SensorChart data={data} onPointClick={handlePointClick} />
            <DataInputForm />
            <DataTable
                data={data}
                onEdit={handleEdit}
                onDelete={handleDelete}
                selectedIndex={selectedIndex}
                onRowClick={handleRowClick} // Pass the handleRowClick function to DataTable
            />
        </div>
    );
}

export default App;
