import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import SensorChart from './components/SensorChart';
import DataInputForm from './components/DataInputForm';
import DataTable from './components/DataTable'; // Import the table component

const socket = io(process.env.REACT_APP_BACKEND_URL);

function App() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACKEND_URL}/api/data`)
            .then(response => response.json())
            .then(data => {
                console.log("Initial data:", data); // Debugging log
                setData(data);
            })
            .catch(err => setError('Failed to fetch initial data'));

        socket.on('update', newData => {
            console.log("Received new data from WebSocket:", newData); // Debugging log
            setData(prevData => [...prevData, newData]);
        });

        socket.on('error', errorMsg => {
            console.error("WebSocket error:", errorMsg); // Error log
            setError(errorMsg.error);
        });

        return () => {
            socket.disconnect();
        };
    }, []);

    return (
        <div className="App">
            <h1>Real-Time Environmental Data Monitoring</h1>
            {error && data.length === 0 && <p className="error">{error}</p>}
            <SensorChart data={data} />
            <DataInputForm />
            <DataTable data={data} /> {/* Add the table component here */}
        </div>
    );
}

export default App;
